mapper.select:
select * from schedule_job


		bean_name like concat('%', #{beanName}, '%') 



limit #{offset}, #{limit}

mapper.update:
update schedule_job
bean_name = #{beanName}, method_name = #{methodName}, params = #{params}, cron_expression = #{cronExpression}, status = #{status}, remark = #{remark},
where job_id = #{jobId}
